INSERT [marathon-se].dbo.Charity(CharityId, CharityName, CharityDescription, CharityLogo) VALUES (1, N'Arise', N'Arise supports women and children who have experienced domestic violence, enabling them to rebuild their lives. The impact of domestic violence is far reaching, impacts the whole community and can continue for generations.

