INSERT [marathon-se].dbo.Charity(CharityId, CharityName, CharityDescription, CharityLogo) VALUES (3, N'Clara Santos Oliveira Institute', N'The Clara Santos Oliveira Institute is a non-governmental, non-profit organisation based in Sao Paulo.  Clara founded the institute in 1986 to provide shelter and education to young people who have been through the juvenile court. Today there are five houses in Sao Paulo and we want to expand and set up another three by 2020.', N'clara-santos-oliveira-institute-logo.png')
INSERT [marathon-se].dbo.Charity(CharityId, CharityName, CharityDescription, CharityLogo) VALUES (4, N'Conquer Cancer - Brazil', N'Conquer Cancer Brazil (CCB) is dedicated to helping discover the treatments and cures for cancer by funding world class research within Brazil. We raise funds exclusively for research into the prevention, diagnosis and treatments for ALL types of cancer, in centres right across Brazil.

Help us work to change the lives of millions of people.', N'conquer-cancer-brazil.png')
INSERT [marathon-se].dbo.Charity(CharityId, CharityName, CharityDescription, CharityLogo) VALUES (5, N'Diabetes Brazil', N'Diabetes Brazil is committed to reducing the impact of diabetes through education, early detection and support.

