INSERT [marathon-se].dbo.Charity(CharityId, CharityName, CharityDescription, CharityLogo) VALUES (6, N'Heart Health Sгo Paulo', N'For over 50 years, Heart Health Sгo Paulo has been dedicated to saving lives by making a difference to the heart health of our people. Donations help us fund valuable research, develop guidelines for health professionals, support patient care and help Paulistas to live healthier lifestyles.', N'heart-health-sao-paulo-logo.png')
INSERT [marathon-se].dbo.Charity(CharityId, CharityName, CharityDescription, CharityLogo) VALUES (7, N'Human Rights Centre  - Sгo Paulo', N'The Human Rights Centre protects and promotes human rights in Brazil and in Brazilian activities overseas.  This is done through legal action, advocacy and research. The HRC is an independent, not-for-profit, non-government organisation and is a registered charity.

